import './Preferences.css';
import React, { useState } from 'react';

function CropPreference() {
    const [text, setText] = useState('Enter your crops');

    const handleClick = () => {
        setText('');
        //text is the local var
    };
    return (
        <div className="preferences flex">
            <p className="textBold padding">What crops are you growing?</p>
            <textarea
                className="input"
                value={text}
                onClick={handleClick}
                onChange={handleChange}
            />
        </div>
    );
}

function handleChange () {
    if(text =="corn"){
        return(
            <table className="table padding">
            <tr>
                <th>Crop</th>
                <th>Ideal soil moisture</th>
            </tr>
            <tr>
                <th>Corn</th>
                <th>0.5</th>
            </tr>
        </table>
        );
    }
}
function Preferences() {
    return (
        <div className="content">
            <div className="top elementContainer">
                <p className="instruction textSmall">Step 2: Set your preferences</p>
            </div>
            <CropPreference />
            <div className="bottom elementContainer">
                <button className="previous textSmall">Previous</button>
                <button className="next textSmall">Next</button>
            </div>
        </div>
    );
}

export default Preferences;
