import './Preferences.css'

function CropPreference(){
    const handleClick = () => {
        textarea.content = "";
    }
    return(
        <div class="preferences flex">
            <p class="textBold">What crops are you growing?</p>
            <textarea class="input" onClick={handleClick}>Enter your crops</textarea>
        </div>
    );
}

function Preferences () {
    return(
        <div class="content">
            <div class="top elementContainer">
                <p class="instruction textSmall">Step 2: Set your preferences</p>
            </div>
            <CropPreference></CropPreference>
            <div class="bottom elementContainer">
                <button class="previous textSmall">Previous</button>
                <button class="next textSmall">Next</button>
            </div>
        </div>
    );
}
export default Preferences;
